import styled from 'styled-components';

export const Container = styled.div`
    width: 100%;
    height: 100vh;
    background-color: #FAFAFA;

    display: flex;
    align-items: center;
    justify-content: center;
`


export const Content = style.div`
    bakground-color: #FFFFFF;
    width: 80%;
    min-height: 350px;    
`
